import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import './styles/Charts.css';

const Charts = ({ data }) => {
  const [selectedSubject, setSelectedSubject] = useState('');

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
  };

  const getCount = (property, value) =>
  filteredData.filter((item) => item[property] === value).length;


  const filteredData = data.filter(
    (item) => !selectedSubject || item.favoriteSubject === selectedSubject
  );

  const chartData = {
    labels: [
      'Class Quality',
      'Internship',
      'Favorite Class',
      'Best Professor',
      'Graduation Date',
    ],
    datasets: [
      {
        label: 'Data',
        data: [
          filteredData.filter(
            (item) => item.classQuality && item.classQuality === 'Excellent'
          ).length,
          filteredData.filter((item) => item.hasInternship === 'Yes').length,
          filteredData.filter(
            (item) => item.favoriteSubject === 'Mathematics'
          ).length,
          filteredData.filter((item) => item.bestProfessor === 'Prof. John').length,
          filteredData.filter((item) => item.graduationDate === '2022').length,
        ],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-3">
          <select className="form-select" onChange={handleSubjectChange}>
            <option value="">Filter by Subject</option>
            <option value="Math">Math</option>
            <option value="Science">Science</option>
            <option value="History">History</option>
            <option value="English">English</option>
            {/* Add more subjects here */}
          </select>
        </div>
        <div className="col-9">
          <Bar data={chartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
};

export default Charts;
