import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import './styles/Charts.css';

const Charts = ({ data }) => {
  const [selectedQuestion, setSelectedQuestion] = useState('');

  const handleSelectChange = (event) => {
    setSelectedQuestion(event.target.value);
  };
  const chartData = {
    labels: [
      'Class Quality',
      'Internship',
      'Favorite Class',
      'Best Professor',
      'Graduation Date',
    ],
    datasets: [
      {
        label: 'Data',
        data: [
          data.filter(
            (item) => item.classQuality && item.classQuality === 'Excellent'
          ).length,
          data.filter((item) => item.hasInternship === 'Yes').length,
          data.filter((item) => item.favoriteSubject === 'Mathematics').length,
          data.filter((item) => item.bestProfessor === 'Prof. John').length,
          data.filter((item) => item.graduationDate === '2022').length,
        ],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };
  

  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-3">
          <select className="form-select" onChange={handleSelectChange}>
            <option value="">Filter by Question</option>
            <option value="Class Quality">Class Quality</option>
            <option value="Internship">Internship</option>
            <option value="Favorite Class">Favorite Class</option>
            <option value="Best Professor">Best Professor</option>
            <option value="Graduation Date">Graduation Date</option>
          </select>
        </div>
        <div className="col-9">
          <Bar data={chartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
};

export default Charts;
