import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import './styles/Charts.css';

const Charts = ({ data }) => {
  const [selectedSubject, setSelectedSubject] = useState('');

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
  };

  const generateChartData = (property, filter) => {
    const filteredData = data.filter((item) => !selectedSubject || item.favoriteSubject === selectedSubject);
    return filter.map((value) => getCount(property, value));
  };
  

  const getCount = (property, value) =>
  filteredData.filter((item) => item[property] === value).length;


  const filteredData = data.filter(
    (item) => !selectedSubject || item.favoriteSubject === selectedSubject
  );

  const chartConfigs = [
    {
      title: 'Best Professor',
      property: 'bestProfessor',
      filter: ['Prof. John', 'Prof. Jane', 'Prof. Smith', 'Prof. Brown'],
    },
    {
      title: 'Class Quality',
      property: 'classQuality',
      filter: ['1', '2', '3', '4', '5'],
    },
    {
      title: 'Internship',
      property: 'hasInternship',
      filter: ['Yes', 'No'],
    },
    {
      title: 'Favorite Class',
      property: 'favoriteSubject',
      filter: ['Math', 'Science', 'History', 'English'],
    },
  ];
  
  const chartData = {
    labels: [
      'Class Quality',
      'Internship',
      'Favorite Class',
      'Best Professor',
      'Graduation Date',
    ],
    datasets: [
      {
        label: 'Data',
        data: [
          getCount('classQuality', 'Excellent'),
          getCount('hasInternship', 'Yes'),
          getCount('favoriteSubject', 'Math'), // Update this line
          getCount('bestProfessor', 'Prof. John'),
          getCount('graduationDate', '2024'), // Update this line
        ],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };
  
  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };
  return (
    <div className="container">
      <div className="row">
        <div className="col-3">
          <select className="form-select" onChange={handleSubjectChange}>
            <option value="">Filter by Subject</option>
            <option value="Math">Math</option>
            <option value="Science">Science</option>
            <option value="History">History</option>
            <option value="English">English</option>
            {/* Add more subjects here */}
          </select>
        </div>
        {chartConfigs.map((config) => (
          <div className="col-9" key={config.title}>
            <h3>{config.title}</h3>
            <Bar
              data={{
                labels: config.filter,
                datasets: [
                  {
                    label: config.title,
                    data: generateChartData(config.property, config.filter),
                    backgroundColor: 'rgba(75, 192, 192, 0.6)',
                  },
                ],
              }}
              options={chartOptions}
            />
          </div>
        ))}
      </div>
    </div>
  );
  

};

export default Charts;
